$('div[fs-field-type="address"]').each(function(e, t) {
            var n = $(t).find(".fsFieldAddress input").attr("id"),
                i = [{
                    element: n,
                    field: "",
                    mode: 1
                }, {
                    element: n,
                    field: "Line1",
                    mode: 2
                }, {
                    element: $(t).find(".fsFieldAddress2 input").attr("id"),
                    field: "Line2",
                    mode: 2
                }, {
                    element: $(t).find(".fsFieldCity input").attr("id"),
                    field: "City",
                    mode: 2
                }, {
                    element: $(t).find(".fsFieldState select").attr("id"),
                    field: "ProvinceName",
                    mode: 2
                }, {
                    element: $(t).find(".fsFieldZip input").attr("id"),
                    field: "PostalCode"
                }];
            new pca.Address(i, {
                key: "KF52-HM45-TC15-RH34"
            }).listen("populate", function(address) {
                let street = address.Line1.split(" ");
                if (street[0]) {
                    street = street[0];
                    unit = street.split("-");
                    if (unit[0]) {
                        unit = unit[0].trim();
                        street = address.Line1.replace(unit + "-", "");
                        if(unit === street.substr(0, unit.length)){
                            street = street.replace(unit, '');
                        }
                        $(t).find('.fsFieldAddress input').val(street);
                        $(t).find('.fsFieldAddress2 input').val("Unit " + unit);
                    }
                }
            })
        })
